mapper.select:
select a.cno, mno, username, content, created_at, modified_at
from (
	select cno from comment
	where bno = #{bno}
	order by cno desc
	limit #{pgvo.pageStart}, #{pgvo.qty}
)a inner join comment b 
on a.cno = b.cno

mapper.update:
update comment
set content = #{content}, modified_at = now()
where cno = #{cno}
